import unittest

from dexter.models import Document, db
from dexter.models.seeds import seed_db
from dexter.processing.crawlers import NamibianCrawler

class TestNamibianCrawler(unittest.TestCase):
    def setUp(self):
        self.crawler = NamibianCrawler()

        self.db = db
        self.db.drop_all()
        self.db.create_all()
        seed_db(db)

    def tearDown(self):
        self.db.session.remove()
        self.db.drop_all()

